const data = msg.payload.results[0].series[0]

const values = data.values;

var chart = {
    labels: [],
    data: []
};


for (var i = 0; i < 10; i++) {
    chart.labels.push(values[i][0])
    chart.data.push(values[i][1])
}

msg.payload = {
    "seriesdata":{
        "labels": chart.labels,
        "datasets": [
            {
                "label": data.tags.eui,
                "backgroundColor": "#f87979",
                "data": chart.data
            }
        ]
    }
}

return msg;

if(msg.urls.length === 0){
     msg.payload = "transmission finished"
    return [msg,null];
}
else{
    var newParameter = msg.urls.pop();
    msg.url = newParameter.url;
    msg.currentParameter = newParameter.parameter;
    return [null,msg]
}

msg.filter = ["GUT", "BIZ", "AMW", "JON"];
msg.urls = [
    {url:"https://data.geo.admin.ch/ch.meteoschweiz.messwerte-globalstrahlung-10min/ch.meteoschweiz.messwerte-globalstrahlung-10min_de.json",
    parameter: "solar_radiation"},
    {url: "https://data.geo.admin.ch/ch.meteoschweiz.messwerte-lufttemperatur-10min/ch.meteoschweiz.messwerte-lufttemperatur-10min_de.json",
    parameter: "temperature"},
    {url: "https://data.geo.admin.ch/ch.meteoschweiz.messwerte-luftdruck-qfe-10min/ch.meteoschweiz.messwerte-luftdruck-qfe-10min_de.json",
    parameter: "barometric_pressure"},
    {url: "https://data.geo.admin.ch/ch.meteoschweiz.messwerte-windgeschwindigkeit-kmh-10min/ch.meteoschweiz.messwerte-windgeschwindigkeit-kmh-10min_de.json",
    parameter: "wind"},
    {url: "https://data.geo.admin.ch/ch.meteoschweiz.messwerte-taupunkt-10min/ch.meteoschweiz.messwerte-taupunkt-10min_de.json",
    parameter: "dew_point"},
    {url: "https://data.geo.admin.ch/ch.meteoschweiz.messwerte-niederschlag-10min/ch.meteoschweiz.messwerte-niederschlag-10min_de.json",
    parameter: "rain"},
    {url: "https://data.geo.admin.ch/ch.meteoschweiz.messwerte-luftfeuchtigkeit-10min/ch.meteoschweiz.messwerte-luftfeuchtigkeit-10min_de.json",
    parameter: "humidity"}
    ];
msg.source = "MeteoSchweiz"
msg.currentParameter=[];
msg.measurement = env.get("MEASUREMENT_weather");
return msg;

Date.prototype.getUnixTime = function() { return this.getTime()/1000|0 };

let token = env.get("ZENNER_TOKEN");
let dbName = env.get("INFLUXDB_DB_Weather "); //swaWeatherStorage.autogen
let endpoint = env.get("INFLUX_ENDPOINT"); //http://swa-meshup_influx_1:8086
var payload = [];

var packetSet = [];
for(var location in msg.payload.features){
    if(msg.filter.includes(msg.payload.features[location].id)){
        var tagSet = {}; 
        var fieldSet = {};
        tagSet.source = msg.source;
        //tagSet.zone = msg.payload.features[location].properties.station_name;
        tagSet.pos_longitude = msg.payload.features[location].geometry.coordinates[0];
        tagSet.pos_latitude = msg.payload.features[location].geometry.coordinates[1];
        //tagSet.pos_alt = parseFloat(msg.payload.features[location].properties.altitude);
        fieldSet[msg.currentParameter] =msg.payload.features[location].properties.value;
        var timestamp = new Date(msg.payload.features[location].properties.reference_ts)
        fieldSet.time =timestamp.getTime()*1000000
        packetSet.push(fieldSet);
        packetSet.push(tagSet);

    }
}
payload.push(packetSet);

msg.payloadWithWrongLocation = payload;
msg.tempPayload = [];
return msg;

var requestBody = ""

for (var i = 0; i < msg.payload.length; i+=2) {
  var valueSet = msg.payload[i];
  var tagSet = msg.payload[i+1];
  var measurement = msg.measurement;

  var valueSetBody="";
  var tagSetBody="";
  var timestamp;
  // Prepare Schema as follows
  //    soil,id=swa-plantcare-16,lon=12;lat=9 soil_temp=20,moisture=60,coolingtime=24 20398340912384
  for(var value in valueSet){
      if(value == "time"){
          timestamp = valueSet[value];
      }else{
          valueSetBody += value+"="+valueSet[value]+",";
      }
  }
  for(var tag in tagSet){
     tagSetBody += tag+"="+tagSet[tag]+",";
  }

  requestBody += measurement +","+tagSetBody.slice(0, -1)+ " "+valueSetBody.slice(0, -1)+" "+timestamp+'\n';
}

msg = {};
msg.payload =new Buffer.from(env.get("INFLUXDB_USER") + ':' + env.get("INFLUXDB_USER_PASSWORD"))
msg.requestBody = requestBody;
return msg;

msg.url = env.get("INFLUX_ENDPOINT")+"/write?db="+env.get("INFLUXDB_DB_Weather");
msg.headers = {};
msg.headers = {"Authorization": 'Basic '+msg.payload};
msg.payload = msg.requestBody
return msg;

if(msg.payloadWithWrongLocation[0].length === 0){

    msg.payload = msg.tempPayload;
    return [msg,null];
}
else{

    var tagSet = (msg.payloadWithWrongLocation[0].pop());
    var keySet = msg.payloadWithWrongLocation[0].pop();
    msg.url = "http://geodesy.geo.admin.ch/reframe/lv95towgs84?easting="+tagSet.pos_longitude+"&northing="+tagSet.pos_latitude;
    msg.tempPayload.push(keySet);
    msg.rawTagSet = tagSet;
    return [null,msg]
}

msg.rawTagSet.pos_longitude = msg.payload.coordinates[0];
msg.rawTagSet.pos_latitude = msg.payload.coordinates[1];
msg.tempPayload.push(msg.rawTagSet);
return msg;

